/*-----------------------------------------------*
  Styling Options
/*-----------------------------------------------*/
function th_load_css() {
	    global $pixi_options;
        // Dark Version
		$th_theme_style =& $pixi_options['th_theme_style'];
		if($th_theme_style != '' && $th_theme_style == 'dark'){
			wp_enqueue_style( 'main-dark-css', PIXI_URI_PATH.'/assets/css/dark.css');
		}
        // Custom colors
		$th_theme_stylesheet =& $pixi_options['th_select_stylesheet']; // Stylesheet select
		if ( $th_theme_stylesheet != '' && $th_theme_stylesheet !== 'default.css'){
			wp_enqueue_style('main-color-theme', PIXI_URI_PATH . '/assets/css/colors/'.$th_theme_stylesheet);
		}
}
add_action('wp_enqueue_scripts', 'th_load_css');
/*-----------------------------------------------*
  Style Inline
/*-----------------------------------------------*/
function pixi_add_style_inline() {
	global $pixi_options;
    $custom_style = null;
	/* Body background */
    $tb_background_color =& $pixi_options['body_background']['background-color']; 
    $tb_background_image =& $pixi_options['body_background']['background-image'];
    $tb_background_repeat =& $pixi_options['body_background']['background-repeat'];
    $tb_background_position =& $pixi_options['body_background']['background-position'];
    $tb_background_size =& $pixi_options['body_background']['background-size'];
    $tb_background_attachment =& $pixi_options['body_background']['background-attachment'];
	$custom_style .= "body{ background-color: $tb_background_color }";
	if($tb_background_image){
		$custom_style .= "body{ background: url('$tb_background_image') $tb_background_repeat $tb_background_attachment $tb_background_position; background-size: $tb_background_size;}";
	}
	/* Title bar background */
    $tb_title_bar_bg_color =& $pixi_options['tb_title_bar_bg']['background-color'];
    $title_bar_bg_image =& $pixi_options['tb_title_bar_bg']['background-image'];
	$bg_title_bar = get_post_meta(get_the_ID(),'tb_bg_title_bar',true);
	if($bg_title_bar) {
		$title_bar_bg_image = $bg_title_bar;
	}
